    /**
     * @group testPostSuccess
     */
    public function testPostSuccessFailData()
    {
        $input   = [];
        $request = new LaravelRequest();
        $request->setMethod(strtoupper("post"));
        $request->replace($input);

        $controller = new APIController();
        $response   = $controller->postSuccess($request);
        $response   = json_decode($response->getContent(), true);

        $this->assertArrayHasKey("message", $response);
        $this->assertArrayHasKey("th", Arr::get($response, 'message'));
        $this->assertArrayHasKey("en", Arr::get($response, 'message'));
        $this->assertEquals('secure_key (order id) not found in input', Arr::get($response, 'message.en'));
        $this->assertEquals('ไม่พบ secure_key (order id) ในข้อมูลนำเข้า', Arr::get($response, 'message.th'));
    }

