        $this->mock_lvc_service->shouldReceive('getPortalCampaign')->once()->andReturn($mock_flash_sale_content);

        Redis::shouldReceive('get')->with('store-cache:api:url:search/product:body:{"page":1,"limit":"10","campaign_id":"16961"}:content')->once()->andReturn(json_encode($mock_flash_sale));
        Redis::shouldReceive('get')->with('store-cache:api:url:search/product:body:{"page":1,"limit":"10","campaign_id":"12858"}:content')->once()->andReturn(json_encode($mock_exclusive));
        Redis::shouldReceive('get')->with('store-cache:api:url:search/product:body:{"page":1,"limit":"10","campaign_id":"16963"}:content')->once()->andReturn(json_encode($mock_best_seller));
        Redis::shouldReceive('get')->with('store-cache:api:url:search/product:body:{"page":1,"limit":"4","campaign_id":"16964"}:content')->once()->andThrow(new \Exception('Internal Server Error'));

        $result = LvAService::getAllContent($input);
        $this->assertEquals(\Arr::get($mock_expect, 'body.config'), \Arr::get($result, 'body.config'));
        $content_data = \Arr::get($mock_expect, 'body.content');
        $this->assertEquals(5, count(\Arr::get($result, 'body.config')));
        $this->assertEquals(5, count(\Arr::get($result, "body.content")));
        foreach ($content_data as $key => $content) {
            $this->assertEquals(count(\Arr::get($content, 'data')), count(\Arr::get($result, "body.content.$key.data")));
            $this->assertEquals(\Arr::get($content, 'data.0'), \Arr::get($result, "body.content.$key.data.0"));
            $this->assertEquals(\Arr::get($content, 'key'), \Arr::get($result, "body.content.$key.key"));
        }
    }

